-- Enable UUID extension
CREATE EXTENSION IF NOT EXISTS "uuid-ossp";

-- Users profile table (extends auth.users)
CREATE TABLE profiles (
  id UUID PRIMARY KEY REFERENCES auth.users(id) ON DELETE CASCADE,
  email TEXT NOT NULL,
  blossoms INTEGER DEFAULT 0,
  level INTEGER DEFAULT 1,
  streak INTEGER DEFAULT 0,
  last_exercise_date DATE,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Enable RLS
ALTER TABLE profiles ENABLE ROW LEVEL SECURITY;

-- RLS Policies for profiles
CREATE POLICY "Users can view own profile" ON profiles FOR SELECT USING (auth.uid() = id);
CREATE POLICY "Users can insert own profile" ON profiles FOR INSERT WITH CHECK (auth.uid() = id);
CREATE POLICY "Users can update own profile" ON profiles FOR UPDATE USING (auth.uid() = id);

-- Exercises completed table
CREATE TABLE exercises_completed (
  id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  hub_name TEXT NOT NULL,
  exercise_name TEXT NOT NULL,
  exercise_index INTEGER NOT NULL,
  rating INTEGER,
  blossoms_earned INTEGER DEFAULT 5,
  completed_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Enable RLS
ALTER TABLE exercises_completed ENABLE ROW LEVEL SECURITY;

-- RLS Policies for exercises_completed
CREATE POLICY "Users can view own exercises" ON exercises_completed FOR SELECT USING (auth.uid() = user_id);
CREATE POLICY "Users can insert own exercises" ON exercises_completed FOR INSERT WITH CHECK (auth.uid() = user_id);

-- Hub sequences completed table (all 5 exercises in a hub)
CREATE TABLE hub_sequences_completed (
  id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  hub_name TEXT NOT NULL,
  blossoms_earned INTEGER DEFAULT 10,
  completed_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Enable RLS
ALTER TABLE hub_sequences_completed ENABLE ROW LEVEL SECURITY;

-- RLS Policies for hub_sequences_completed
CREATE POLICY "Users can view own sequences" ON hub_sequences_completed FOR SELECT USING (auth.uid() = user_id);
CREATE POLICY "Users can insert own sequences" ON hub_sequences_completed FOR INSERT WITH CHECK (auth.uid() = user_id);

-- Create indexes for better performance
CREATE INDEX idx_exercises_completed_user_id ON exercises_completed(user_id);
CREATE INDEX idx_exercises_completed_date ON exercises_completed(completed_at);
CREATE INDEX idx_hub_sequences_user_id ON hub_sequences_completed(user_id);
CREATE INDEX idx_profiles_user_id ON profiles(id);
